mapper.update:
        UPDATE music
           SET u_t = UNIX_TIMESTAMP(now())
			  ,soundsize = #{soundsize}
         WHERE id = #{id,jdbcType=INTEGER}

        UPDATE music
           SET u_t = UNIX_TIMESTAMP(now())
			  ,collects = #{collects}
         WHERE is_deleted = 0
           AND id = #{id,jdbcType=INTEGER}

        UPDATE music
           SET u_t = UNIX_TIMESTAMP(now())
			  ,name = #{name},author = #{author}
         WHERE is_deleted = 0
           AND id = #{id,jdbcType=INTEGER}
